public class Grades{    String name, ID;    int lab1, lab2, lab3, midTerm, finalExam, totalGrade;    /**     * Grades(data)     *     * constructor to initialize variable.     * **/    public Grades(String[] data){        ID = data[0];        name = data[1];        lab1 = Integer.parseInt(data[2]);        lab2 = Integer.parseInt(data[3]);        lab3 = Integer.parseInt(data[4]);        midTerm = Integer.parseInt(data[5]);        finalExam = Integer.parseInt(data[6]);        totalGrade = 0;    }    /**     * calculateTotalGrade(weights)     *     * @param weights     * **/    public void calculateTotalGrade(float[] weights){        totalGrade = Math.round(weights[0] * lab1 + weights[1] * lab2 + weights[2] * lab3 + weights[3] * midTerm + weights[4] * finalExam);    }    public String getName(){        return name;    }    public String getID(){        return ID;    }    public int getLab1(){        return lab1;    }    public int getLab2(){        return lab2;    }    public int getLab3(){        return lab3;    }    public int getMidTerm(){        return midTerm;    }    public int getFinalExam(){        return finalExam;    }    public int getTotalGrade(){        return totalGrade;    }}